import torch

# transpose helper
def transpose2(m):
    return torch.transpose(m, 0, 1)

# helper to generate random matrix
def genRandomMat(sizeX,sizeY,  rangeMin, rangeMax):
    return (rangeMax-rangeMin)*torch.rand(sizeX, sizeY)+(rangeMin)


